<?php defined('SYSPATH') or die('No direct script access.');class Flash extends	Flash_Flash{		public function __get($name){		$message = parent::__get($name);		if(!empty($message)){		  return "<div class='notification {$name}'><p><strong>Info:</strong>  {$message}</p></div>";		}		return null;	}	  public function all(){    foreach(get_object_vars(parent::instance()) as $name => $message){      return "<div class='notification {$name}'><p><strong>Info:</strong>  {$message}</p></div>";    };  }}